import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "../Pages/Blogs.module.css";

const BASE_URL = "http://localhost:5000";  

function Blogs() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`${BASE_URL}/api/blogs`)
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setBlogs(data))
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p className={styles.loading}>Loading blogs...</p>;
  if (error) return <p className={styles.error}>Error: {error}</p>;
  if (blogs.length === 0) return <p className={styles.noBlogs}>No blogs found.</p>;

  return (
    <section id="blogs" className={styles.maincontainer}>
      <div className={styles.recent}>
        <h2>PROFESSIONAL </h2>
        <h2 className={styles.pro}>UPDATES</h2>
      </div>

      {/* <div className={styles.linkedinContainer}>
        <div className={styles.linkedinCard}>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:7308842570082619392?collapsed=1" height="520" width="340" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:7307796484622532608?collapsed=1" height="520" width="340" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:7322250673570402304?collapsed=1" height="520" width="340" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:share:7311952544468017153?collapsed=1" height="633" width="504" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:share:7323001073848209409?collapsed=1" height="605" width="504" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
        </div>

        <div aria-hidden className={styles.linkedinCard}>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:7308842570082619392?collapsed=1" height="520" width="340" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:7307796484622532608?collapsed=1" height="520" width="340" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:7322250673570402304?collapsed=1" height="520" width="340" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:7307796484622532608?collapsed=1" height="520" width="340" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:7322250673570402304?collapsed=1" height="520" width="340" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
          <iframe className={styles.card} src="https://www.linkedin.com/embed/feed/update/urn:li:share:7311952544468017153?collapsed=1" height="633" width="504" frameBorder="0" allowFullScreen title="Embedded post"></iframe>
        </div>
      </div> */}

      <div className={styles.recent}>
        <h2 className={styles.head2}>FROM</h2>
        <h2 className={styles.pro}>MY LENS</h2>
      </div>

      <div className={styles.blogcontainer}>
        {blogs.map((blog) => (
          <div
            key={blog._id}
            className={styles.expone}
            onClick={() => navigate(`/blog/${blog._id}`)}
          >
            {blog.image && (
              <img 
                src={blog.image} 
                alt={blog.title} 
                className={styles.blogImage}
              />
            )}
            <h3 className={styles.exphead}>{blog.title}</h3>
            <p className={styles.exppara}>
              {blog.content.slice(0, 150)}...
            </p>
            <p className={styles.expnum}>{blog.date}</p>
          </div>
        ))}
      </div>

    </section>
  );
}

export default Blogs;
