import React, { useState, useEffect } from "react";
import styles from "./Admin.module.css";

const BASE_URL = "http://localhost:5000";

function Admin() {
  const [blog, setBlog] = useState({ title: "", content: "", image: null });
  const [blogs, setBlogs] = useState([]);
  const [editingBlogId, setEditingBlogId] = useState(null);

  const [project, setProject] = useState({
    name: "",
    description: "",
    image: null,
    link: "",
  });
  const [projects, setProjects] = useState([]);
  const [editingProjectId, setEditingProjectId] = useState(null);

  const [expandedItems, setExpandedItems] = useState({});
  const [popup, setPopup] = useState({ show: false, type: "", id: null });

  // ---------------- FETCH DATA ----------------
  const fetchBlogs = async () => {
    const res = await fetch(`${BASE_URL}/api/blogs`);
    const data = await res.json();
    setBlogs(data);
  };

  const fetchProjects = async () => {
    const res = await fetch(`${BASE_URL}/api/projects`);
    const data = await res.json();
    setProjects(data);
  };

  useEffect(() => {
    fetchBlogs();
    fetchProjects();
  }, []);

  // ---------------- HANDLE INPUTS ----------------
  const handleBlogChange = (e) => {
    setBlog({ ...blog, [e.target.name]: e.target.value });
  };

  const handleProjectChange = (e) => {
    setProject({ ...project, [e.target.name]: e.target.value });
  };

  const handleBlogImage = (e) => {
    setBlog({ ...blog, image: e.target.files[0] });
  };

  const handleProjectImage = (e) => {
    setProject({ ...project, image: e.target.files[0] });
  };

  // ---------------- SUBMIT BLOG ----------------
  const handleBlogSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", blog.title);
    formData.append("content", blog.content);
    if (blog.image) formData.append("image", blog.image);

    const url = editingBlogId
      ? `${BASE_URL}/api/blogs/${editingBlogId}`
      : `${BASE_URL}/api/blogs`;

    const method = editingBlogId ? "PUT" : "POST";

    const res = await fetch(url, { method, body: formData });

    setBlog({ title: "", content: "", image: null });
    setEditingBlogId(null);

    fetchBlogs();
  };

  // ---------------- SUBMIT PROJECT ----------------
  const handleProjectSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", project.name);
    formData.append("description", project.description);
    formData.append("link", project.link);
    if (project.image) formData.append("image", project.image);

    const url = editingProjectId
      ? `${BASE_URL}/api/projects/${editingProjectId}`
      : `${BASE_URL}/api/projects`;

    const method = editingProjectId ? "PUT" : "POST";

    await fetch(url, { method, body: formData });

    setProject({ name: "", description: "", image: null, link: "" });
    setEditingProjectId(null);

    fetchProjects();
  };

  // ---------------- DELETE ITEM ----------------
  const handleDelete = async () => {
    const { type, id } = popup;
    const url =
      type === "blog"
        ? `${BASE_URL}/api/blogs/${id}`
        : `${BASE_URL}/api/projects/${id}`;

    await fetch(url, { method: "DELETE" });

    if (type === "blog") fetchBlogs();
    else fetchProjects();

    setPopup({ show: false, type: "", id: null });
  };

  // ---------------- EDIT ITEMS ----------------
  const editBlog = (b) => {
    setBlog({ title: b.title, content: b.content, image: null });
    setEditingBlogId(b._id);
    window.scrollTo(0, 0);
  };

  const editProject = (p) => {
    setProject({
      name: p.name,
      description: p.description,
      link: p.link,
      image: null,
    });
    setEditingProjectId(p._id);
    window.scrollTo(0, 0);
  };

  // ---------------- EXPAND CONTENT ----------------
  const toggleExpand = (id) => {
    setExpandedItems({ ...expandedItems, [id]: !expandedItems[id] });
  };

  const truncateText = (text, id) => {
    if (expandedItems[id]) return text;
    return text.length > 100 ? text.slice(0, 100) + "..." : text;
  };

  return (
    <div className={styles.adminContainer}>
      {/* FORM SECTION */}
      <div className={styles.formSection}>
        <h2>Blogs</h2>
        <form onSubmit={handleBlogSubmit}>
          <input
            type="text"
            name="title"
            placeholder="Blog Title"
            value={blog.title}
            onChange={handleBlogChange}
            required
          />
          <textarea
            name="content"
            placeholder="Blog Content"
            value={blog.content}
            onChange={handleBlogChange}
            required
          />
          <input type="file" accept="image/*" onChange={handleBlogImage} />
          <button type="submit">
            {editingBlogId ? "Update Blog" : "Add Blog"}
          </button>
        </form>

        <h2>Projects</h2>
        <form onSubmit={handleProjectSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Project Name"
            value={project.name}
            onChange={handleProjectChange}
            required
          />
          <textarea
            name="description"
            placeholder="Project Description"
            value={project.description}
            onChange={handleProjectChange}
            required
          />
          <input
            type="text"
            name="link"
            placeholder="Project Link"
            value={project.link}
            onChange={handleProjectChange}
          />
          <input type="file" accept="image/*" onChange={handleProjectImage} />
          <button type="submit">
            {editingProjectId ? "Update Project" : "Add Project"}
          </button>
        </form>
      </div>

      {/* LIST SECTION */}
      <div className={styles.listSection}>
        <h2>Blogs</h2>
        <div className={styles.cardGrid}>
          {blogs.map((b) => (
            <div className={styles.card} key={b._id}>
              {b.image && <img src={b.image} alt={b.title} />}
              <h3>{b.title}</h3>
              <p>{truncateText(b.content, b._id)}</p>
              {b.content.length > 100 && (
                <button onClick={() => toggleExpand(b._id)} className={styles.readBtn}>
                  {expandedItems[b._id] ? "Show Less" : "Read More"}
                </button>
              )}
              <div className={styles.cardActions}>
                <button onClick={() => editBlog(b)}>Edit</button>
                <button onClick={() => setPopup({ show: true, type: "blog", id: b._id })}>
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>

        <h2>Projects</h2>
        <div className={styles.cardGrid}>
          {projects.map((p) => (
            <div className={styles.card} key={p._id}>
              {p.image && <img src={p.image} alt={p.name} />}
              <h3>{p.name}</h3>
              <p>{truncateText(p.description, p._id)}</p>
              {p.description.length > 100 && (
                <button onClick={() => toggleExpand(p._id)} className={styles.readBtn}>
                  {expandedItems[p._id] ? "Show Less" : "Read More"}
                </button>
              )}
              {p.link && <a href={p.link}>Visit</a>}
              <div className={styles.cardActions}>
                <button onClick={() => editProject(p)}>Edit</button>
                <button
                  onClick={() => setPopup({ show: true, type: "project", id: p._id })}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* DELETE POPUP */}
      {popup.show && (
        <div className={styles.popupOverlay}>
          <div className={styles.popup}>
            <p>Are you sure you want to delete this {popup.type}?</p>

            <div className={styles.popupActions}>
              <button onClick={handleDelete}>Yes</button>
              <button onClick={() => setPopup({ show: false, type: "", id: null })}>
                No
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Admin;
